    <script>
        document.addEventListener("DOMContentLoaded", () => { feather.replace(); const observer = new IntersectionObserver((entries) => { entries.forEach(entry => { if (entry.isIntersecting) { entry.target.classList.add('fade-in-visible'); } }); }, { threshold: 0.1 }); document.querySelectorAll('.fade-in').forEach(el => observer.observe(el)); });
        function toggleFaq(element) { const content = element.querySelector('.faq-content'); const icon = element.querySelector('.faq-icon'); const isOpen = content.style.maxHeight && content.style.maxHeight !== '0px'; document.querySelectorAll('.faq-item').forEach(item => { item.querySelector('.faq-content').style.maxHeight = '0px'; item.querySelector('.faq-icon').setAttribute('data-feather', 'plus'); }); if (!isOpen) { content.style.maxHeight = content.scrollHeight + 'px'; icon.setAttribute('data-feather', 'minus'); } feather.replace(); }
    </script>
